int main()
{
	int n;
	int count = 0;
	int count0 = 0;
	double sum = 0.0;
	scanf("%d", &n);
	int arr[100000];
		int i;
		for (i = 0;i < n;i++)
		{
			scanf("%d ", &arr[i]);
		}
		for (i = 0;i < n;i++)
		{
			if (arr[i]< 0)
			{
				count++;
			}
			else if (arr[i] == 0)
			{
				count0++;
			}
			else
			{
				sum += (double)i;
			}
		}
		
		printf("%d %lf.1", count, sum / (n-count0-count));

	return 0;
}